define('@weex-component/item-horizontal', function (require, exports, module) {

;
	module.exports = {
		data: function () {return {
			item:{
				"imgUrl":"http://img.alicdn.com/tps/i2/TB1roT2MXXXXXbZXpXXcxGJ1pXX-108-108.jpg",
				"title":"影逸测试影逸测试影逸测试影逸测试影逸测试",
				"price":"100",
				"itemId":"527510910768"
			},
			status:{
				isCollected:false,
				collectedImg:"http://img.alicdn.com/tps/i2/TB1jWceMXXXXXXqXXXXUdX55FXX-121-102.png",
				unCollectImg:"http://img.alicdn.com/tps/i1/TB139n5MXXXXXbFXpXXUdX55FXX-121-102.png",
				collectImage:"",
				shown:true
			}
		}},
		methods: {
			ready: function (){
				if(this.status.isCollected){
					this.status.collectImage = this.status.collectedImg;
				}else{
					this.status.collectImage = this.status.unCollectImg;
				}
			},
			goTargetUrl: function(e){
				this.$openURL(this.item.h5Url);
			},
			close: function (){
				this.status.shown = false;
			},
			collect: function (e){
				if(this.status.isCollected){
					this._delCollect();
				}else{
					this._addCollect();
				}

			},
			_addCollect: function (){
				var _this = this;

				this.$sendMtop({
					api:"com.taobao.mcl.fav.addCollect",
					v:"2.0",
					param:{
						"itemId":this.item.itemId,
						"favType":"1"
					}
				}, function (e){
					var result = e.result;

					_this.status.isCollected = true;
					_this.status.collectImage = _this.status.collectedImg
				});
			},
			_delCollect: function (){
				var _this = this;

				this.$sendMtop({
					api:"com.taobao.mcl.fav.delcollect",
					v:"2.0",
					param:{
						"itemId":this.item.itemId,
						"favType":"1"
					}
				}, function (e){
					var result = e.result;

					_this.status.isCollected = false;
					_this.status.collectImage = _this.status.unCollectImg
				});	
			}
		}
	};


