  },
  "price": {
    "fontSize": 24,
    "color": "#ff5000"
  },
  "collect": {
    "width": 121,
    "height": 106,
    "marginTop": 12,
    "borderLeft": "1px solid #e8e8e8"
  },
  "close": {
    "width": 60,
    "background": "url(\"http://img.alicdn.com/tps/i1/TB1WlL9MXXXXXXPXpXXqsJyFVXX-26-26.png\") center center no-repeat",
    "backgroundColor": "rgba(0,0,0,0.5)"
  }
}

;module.exports.template = {
  "type": "container",
  "children": [
    {
      "type": "container",
      "classList": [
        "item-container"
      ],
      "shown": function () {return this.status.shown},
      "children": [
        {
          "type": "container",
          "classList": [
            "item-wrap"
          ],
          "children": [
            {
              "type": "image",
              "classList": [
                "item-image"
              ],
              "attr": {
                "src": function () {return this.item.imgUrl}
              },
              "events": {
                "click": "goTargetUrl"
              }
            },
            {
              "type": "container",
              "classList": [
                "item-info"
              ],
              "events": {
                "click": "goTargetUrl"
              },
              "children": [
                {
                  "type": "text",
                  "classList": [
                    "title"
                  ],
                  "attr": {
                    "value": function () {return this.item.title}
                  }
                },
                {
                  "type": "text",
                  "classList": [
                    "price"
                  ],
                  "attr": {
                    "value": function () {return '¥' + (this.item.price)}
                  }
                }
              ]
            },
            {
              "type": "image",
              "classList": [
                "collect"
              ],
              "attr": {
                "src": function () {return this.status.collectImage}
              },
              "events": {
                "click": "collect"
              }
            }
          ]
        },
        {
          "type": "container",
          "classList": [
            "close"
          ],
          "events": {
            "click": "close"
          }
        }
      ]
    }
  ]
}

;})

